mapper.select:
SELECT DISTINCT document.*, u1.username create_name, u2.username modify_name FROM document LEFT JOIN recycle ON document.id = recycle.id, user_info, USER u1 RIGHT JOIN document d ON d.create_id = u1.id LEFT JOIN USER u2 ON d.modify_id = u2.id WHERE recycle.delete_time IS NULL AND document.dept_id = #{deptId}
AND document.file_path = #{filePath}
AND d.id = document.id;
